import React, { useState, useEffect } from 'react';
import { PlusIcon, EyeIcon, PencilIcon, TrashIcon, CheckCircleIcon, XCircleIcon, ClockIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';

const LinkedInAccounts = () => {
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingAccount, setEditingAccount] = useState(null);
  const [formData, setFormData] = useState({
    accountName: '',
    email: '',
    username: '',
    sessionCookie: '',
    proxyUrl: '',
    proxyType: 'http',
    proxyUsername: '',
    proxyPassword: '',
    dailyRequestLimit: 150,
    minDelaySeconds: 30,
    maxDelaySeconds: 90
  });
  const [errors, setErrors] = useState({});
  const [testingProxy, setTestingProxy] = useState(false);
  const [validatingAccount, setValidatingAccount] = useState(null);
  const { user } = useAuth();

  const statusColors = {
    valid: 'text-green-600 bg-green-100',
    invalid: 'text-red-600 bg-red-100',
    expired: 'text-yellow-600 bg-yellow-100',
    blocked: 'text-red-600 bg-red-100',
    pending: 'text-gray-600 bg-gray-100'
  };

  const proxyStatusColors = {
    active: 'text-green-600',
    failed: 'text-red-600',
    testing: 'text-yellow-600'
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = async () => {
    try {
      setLoading(true);
      const response = await api.get('/linkedin-accounts');
      if (response.data.success) {
        setAccounts(response.data.data || []);
      }
    } catch (error) {
      console.error('Failed to fetch accounts:', error);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      accountName: '',
      email: '',
      username: '',
      sessionCookie: '',
      proxyUrl: '',
      proxyType: 'http',
      proxyUsername: '',
      proxyPassword: '',
      dailyRequestLimit: 150,
      minDelaySeconds: 30,
      maxDelaySeconds: 90
    });
    setErrors({});
    setEditingAccount(null);
  };

  const handleOpenModal = (account = null) => {
    if (account) {
      setEditingAccount(account);
      setFormData({
        accountName: account.account_name || '',
        email: account.email || '',
        username: account.username || '',
        sessionCookie: '', // Don't populate for security
        proxyUrl: account.proxy_url || '',
        proxyType: account.proxy_type || 'http',
        proxyUsername: account.proxy_username || '',
        proxyPassword: '', // Don't populate for security
        dailyRequestLimit: account.daily_request_limit || 150,
        minDelaySeconds: account.min_delay_seconds || 30,
        maxDelaySeconds: account.max_delay_seconds || 90
      });
    } else {
      resetForm();
    }
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    resetForm();
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.accountName.trim()) {
      newErrors.accountName = 'Account name is required';
    }

    if (!editingAccount && !formData.sessionCookie.trim()) {
      newErrors.sessionCookie = 'Session cookie is required';
    }

    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (formData.proxyUrl && !/^(https?|socks5):\/\/.+/.test(formData.proxyUrl)) {
      newErrors.proxyUrl = 'Please enter a valid proxy URL';
    }

    if (formData.dailyRequestLimit < 1 || formData.dailyRequestLimit > 1000) {
      newErrors.dailyRequestLimit = 'Daily limit must be between 1 and 1000';
    }

    if (formData.minDelaySeconds < 10 || formData.minDelaySeconds > 300) {
      newErrors.minDelaySeconds = 'Min delay must be between 10 and 300 seconds';
    }

    if (formData.maxDelaySeconds < formData.minDelaySeconds || formData.maxDelaySeconds > 600) {
      newErrors.maxDelaySeconds = 'Max delay must be greater than min delay and less than 600 seconds';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const payload = { ...formData };

      // Remove empty fields
      Object.keys(payload).forEach(key => {
        if (payload[key] === '') {
          delete payload[key];
        }
      });

      if (editingAccount) {
        await api.put(`/linkedin-accounts/${editingAccount.id}`, payload);
      } else {
        await api.post('/linkedin-accounts', payload);
      }

      await fetchAccounts();
      handleCloseModal();
    } catch (error) {
      console.error('Failed to save account:', error);
      setErrors({ submit: 'Failed to save account. Please try again.' });
    }
  };

  const handleDelete = async (accountId) => {
    if (!window.confirm('Are you sure you want to delete this account? This action cannot be undone.')) {
      return;
    }

    try {
      await api.delete(`/linkedin-accounts/${accountId}`);
      await fetchAccounts();
    } catch (error) {
      console.error('Failed to delete account:', error);
      alert('Failed to delete account. Please try again.');
    }
  };

  const handleValidateAccount = async (accountId) => {
    try {
      setValidatingAccount(accountId);
      await api.post(`/linkedin-accounts/${accountId}/validate`);
      await fetchAccounts(); // Refresh to get updated validation status
    } catch (error) {
      console.error('Failed to validate account:', error);
      alert('Failed to validate account. Please try again.');
    } finally {
      setValidatingAccount(null);
    }
  };

  const handleTestProxy = async () => {
    if (!formData.proxyUrl) {
      setErrors({ proxyUrl: 'Please enter a proxy URL to test' });
      return;
    }

    try {
      setTestingProxy(true);
      const response = await api.post('/linkedin-accounts/test-proxy', {
        proxyUrl: formData.proxyUrl,
        proxyUsername: formData.proxyUsername,
        proxyPassword: formData.proxyPassword
      });

      if (response.data.success && response.data.data.success) {
        alert(`Proxy test successful! Response time: ${response.data.data.responseTime}ms`);
      } else {
        alert(`Proxy test failed: ${response.data.data.error}`);
      }
    } catch (error) {
      console.error('Failed to test proxy:', error);
      alert('Failed to test proxy. Please check your settings.');
    } finally {
      setTestingProxy(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Never';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getAccountStatus = (account) => {
    if (account.isBlocked) return 'Blocked';
    if (account.isOnCooldown) return 'Cooldown';
    if (!account.is_active) return 'Inactive';
    return account.validation_status || 'Unknown';
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">LinkedIn Accounts</h1>
          <p className="text-gray-600 mt-1">
            Manage your LinkedIn accounts for automated scraping
          </p>
        </div>
        <button 
          onClick={() => handleOpenModal()}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors flex items-center space-x-2"
        >
          <PlusIcon className="h-5 w-5" />
          <span>Add Account</span>
        </button>
      </div>

      {/* Accounts Table */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        {loading ? (
          <div className="p-8 text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
            <p className="text-gray-600 mt-2">Loading accounts...</p>
          </div>
        ) : accounts.length === 0 ? (
          <div className="p-8 text-center">
            <p className="text-gray-600 mb-4">No LinkedIn accounts configured yet.</p>
            <button 
              onClick={() => handleOpenModal()}
              className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors"
            >
              Add Your First Account
            </button>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Account
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Usage Today
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Proxy
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Last Validated
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {accounts.map((account) => (
                  <tr key={account.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-gray-900">
                          {account.account_name}
                        </div>
                        {account.email && (
                          <div className="text-sm text-gray-500">{account.email}</div>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                        statusColors[account.validation_status] || 'bg-gray-100 text-gray-800'
                      }`}>
                        {getAccountStatus(account)}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">
                        {account.requests_today || 0} / {account.daily_request_limit}
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                        <div 
                          className="bg-blue-500 h-2 rounded-full" 
                          style={{ 
                            width: `${Math.min((account.requests_today || 0) / account.daily_request_limit * 100, 100)}%` 
                          }}
                        ></div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {account.proxy_url ? (
                        <div>
                          <span className={`text-sm ${
                            proxyStatusColors[account.proxy_status] || 'text-gray-600'
                          }`}>
                            {account.proxy_type?.toUpperCase()}
                          </span>
                        </div>
                      ) : (
                        <span className="text-sm text-gray-400">No proxy</span>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-600">
                      {formatDate(account.last_validated_at)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <div className="flex space-x-2">
                        <button 
                          onClick={() => handleValidateAccount(account.id)}
                          disabled={validatingAccount === account.id}
                          className="text-green-600 hover:text-green-900 transition-colors disabled:opacity-50"
                          title="Validate Account"
                        >
                          {validatingAccount === account.id ? (
                            <ClockIcon className="h-4 w-4 animate-spin" />
                          ) : (
                            <CheckCircleIcon className="h-4 w-4" />
                          )}
                        </button>
                        <button 
                          onClick={() => handleOpenModal(account)}
                          className="text-blue-600 hover:text-blue-900 transition-colors"
                          title="Edit Account"
                        >
                          <PencilIcon className="h-4 w-4" />
                        </button>
                        <button 
                          onClick={() => handleDelete(account.id)}
                          className="text-red-600 hover:text-red-900 transition-colors"
                          title="Delete Account"
                        >
                          <TrashIcon className="h-4 w-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Add/Edit Account Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <div className="flex items-center justify-between p-6 border-b">
              <h2 className="text-xl font-semibold text-gray-900">
                {editingAccount ? 'Edit LinkedIn Account' : 'Add LinkedIn Account'}
              </h2>
              <button
                onClick={handleCloseModal}
                className="text-gray-400 hover:text-gray-600 transition-colors"
              >
                <XCircleIcon className="h-6 w-6" />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="p-6">
              {/* Account Information */}
              <div className="mb-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Account Information</h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Account Name *
                    </label>
                    <input
                      type="text"
                      value={formData.accountName}
                      onChange={(e) => setFormData(prev => ({ ...prev, accountName: e.target.value }))}
                      className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                        errors.accountName ? 'border-red-500' : 'border-gray-300'
                      }`}
                      placeholder="My LinkedIn Account"
                    />
                    {errors.accountName && (
                      <p className="text-red-500 text-sm mt-1">{errors.accountName}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Email (Optional)
                    </label>
                    <input
                      type="email"
                      value={formData.email}
                      onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                      className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                        errors.email ? 'border-red-500' : 'border-gray-300'
                      }`}
                      placeholder="user@example.com"
                    />
                    {errors.email && (
                      <p className="text-red-500 text-sm mt-1">{errors.email}</p>
                    )}
                  </div>
                </div>
              </div>

              {/* Session Cookie */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  LinkedIn Session Cookie (li_at) {!editingAccount && '*'}
                </label>
                <textarea
                  value={formData.sessionCookie}
                  onChange={(e) => setFormData(prev => ({ ...prev, sessionCookie: e.target.value }))}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none ${
                    errors.sessionCookie ? 'border-red-500' : 'border-gray-300'
                  }`}
                  rows={3}
                  placeholder="Paste your LinkedIn li_at cookie value here"
                />
                {errors.sessionCookie && (
                  <p className="text-red-500 text-sm mt-1">{errors.sessionCookie}</p>
                )}
                <p className="text-gray-500 text-xs mt-1">
                  Find this in your browser's developer tools under Application → Cookies → linkedin.com → li_at
                </p>
              </div>

              {/* Proxy Configuration */}
              <div className="mb-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Proxy Configuration</h3>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Proxy URL
                    </label>
                    <input
                      type="text"
                      value={formData.proxyUrl}
                      onChange={(e) => setFormData(prev => ({ ...prev, proxyUrl: e.target.value }))}
                      className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                        errors.proxyUrl ? 'border-red-500' : 'border-gray-300'
                      }`}
                      placeholder="http://proxy.example.com:8080"
                    />
                    {errors.proxyUrl && (
                      <p className="text-red-500 text-sm mt-1">{errors.proxyUrl}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Proxy Type
                    </label>
                    <select
                      value={formData.proxyType}
                      onChange={(e) => setFormData(prev => ({ ...prev, proxyType: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="http">HTTP</option>
                      <option value="https">HTTPS</option>
                      <option value="socks5">SOCKS5</option>
                    </select>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Proxy Username
                    </label>
                    <input
                      type="text"
                      value={formData.proxyUsername}
                      onChange={(e) => setFormData(prev => ({ ...prev, proxyUsername: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Optional"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Proxy Password
                    </label>
                    <input
                      type="password"
                      value={formData.proxyPassword}
                      onChange={(e) => setFormData(prev => ({ ...prev, proxyPassword: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Optional"
                    />
                  </div>
                </div>

                {formData.proxyUrl && (
                  <div className="mt-4">
                    <button
                      type="button"
                      onClick={handleTestProxy}
                      disabled={testingProxy}
                      className="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 transition-colors disabled:opacity-50"
                    >
                      {testingProxy ? 'Testing...' : 'Test Proxy'}
                    </button>
                  </div>
                )}
              </div>

              {/* Rate Limiting */}
              <div className="mb-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Rate Limiting</h3>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Daily Request Limit
                    </label>
                    <input
                      type="number"
                      min="1"
                      max="1000"
                      value={formData.dailyRequestLimit}
                      onChange={(e) => setFormData(prev => ({ ...prev, dailyRequestLimit: parseInt(e.target.value) }))}
                      className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                        errors.dailyRequestLimit ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors.dailyRequestLimit && (
                      <p className="text-red-500 text-sm mt-1">{errors.dailyRequestLimit}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Min Delay (seconds)
                    </label>
                    <input
                      type="number"
                      min="10"
                      max="300"
                      value={formData.minDelaySeconds}
                      onChange={(e) => setFormData(prev => ({ ...prev, minDelaySeconds: parseInt(e.target.value) }))}
                      className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                        errors.minDelaySeconds ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors.minDelaySeconds && (
                      <p className="text-red-500 text-sm mt-1">{errors.minDelaySeconds}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Max Delay (seconds)
                    </label>
                    <input
                      type="number"
                      min="30"
                      max="600"
                      value={formData.maxDelaySeconds}
                      onChange={(e) => setFormData(prev => ({ ...prev, maxDelaySeconds: parseInt(e.target.value) }))}
                      className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                        errors.maxDelaySeconds ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors.maxDelaySeconds && (
                      <p className="text-red-500 text-sm mt-1">{errors.maxDelaySeconds}</p>
                    )}
                  </div>
                </div>
              </div>

              {errors.submit && (
                <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
                  {errors.submit}
                </div>
              )}

              {/* Action Buttons */}
              <div className="flex justify-end space-x-3 pt-4 border-t">
                <button
                  type="button"
                  onClick={handleCloseModal}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-6 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                >
                  {editingAccount ? 'Update Account' : 'Add Account'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default LinkedInAccounts;
