import React, { useState, useEffect } from 'react';
import { XMarkIcon, DocumentArrowUpIcon, MagnifyingGlassIcon, BuildingOfficeIcon, UserIcon, UsersIcon } from '@heroicons/react/24/outline';
import api from '../../services/api';

const NewJobModal = ({ isOpen, onClose, onSubmit }) => {
  const [step, setStep] = useState(1);
  const [jobType, setJobType] = useState('');
  const [formData, setFormData] = useState({
    jobType: '',
    file: null,
    searchQuery: '',
    proxyUrl: '',
    linkedinCookie: '',
    jobName: '',
    accountSelectionMode: 'rotation', // New: 'rotation', 'specific', 'manual'
    selectedAccountIds: [],
    accountUid: '', // New: specific account UID
    useAccountRotation: true, // New: enable automatic rotation
    autoRotateAccounts: true,
    maxAccountsPerJob: 1
  });
  const [availableAccounts, setAvailableAccounts] = useState([]);
  const [errors, setErrors] = useState({});
  const [dragActive, setDragActive] = useState(false);

  const jobTypes = [
    {
      id: 'profile',
      name: 'Scrape LinkedIn Profiles',
      description: 'Extract profile data from LinkedIn profile URLs',
      icon: UserIcon,
      color: 'bg-blue-500',
      inputType: 'file',
      fileTypes: 'CSV/Excel with LinkedIn profile URLs'
    },
    {
      id: 'company',
      name: 'Scrape Company Profiles',
      description: 'Extract company data from LinkedIn company URLs',
      icon: BuildingOfficeIcon,
      color: 'bg-green-500',
      inputType: 'file',
      fileTypes: 'CSV/Excel with LinkedIn company URLs'
    },
    {
      id: 'search',
      name: 'Scrape Search Results',
      description: 'Search LinkedIn and collect profile URLs from results',
      icon: MagnifyingGlassIcon,
      color: 'bg-purple-500',
      inputType: 'query',
      fileTypes: 'Search keyword or query'
    }
  ];

  useEffect(() => {
    if (isOpen) {
      fetchAvailableAccounts();
    }
  }, [isOpen]);

  const fetchAvailableAccounts = async () => {
    try {
      const response = await api.get('/api/linkedin-accounts/available');
      if (response.data.success) {
        setAvailableAccounts(response.data.data || []);
      }
    } catch (error) {
      console.error('Failed to fetch available accounts:', error);
      setAvailableAccounts([]);
    }
  };

  const resetForm = () => {
    setStep(1);
    setJobType('');
    setFormData({
      jobType: '',
      file: null,
      searchQuery: '',
      proxyUrl: '',
      linkedinCookie: '',
      jobName: '',
      accountSelectionMode: 'manual',
      selectedAccountIds: [],
      autoRotateAccounts: false,
      maxAccountsPerJob: 1
    });
    setErrors({});
    setDragActive(false);
  };

  const handleClose = () => {
    resetForm();
    onClose();
  };

  const handleJobTypeSelect = (type) => {
    setJobType(type);
    setFormData(prev => ({ ...prev, jobType: type }));
    setStep(2);
  };

  const handleFileUpload = (file) => {
    if (file) {
      const allowedTypes = ['.csv', '.xlsx', '.xls'];
      const fileExtension = file.name.toLowerCase().substring(file.name.lastIndexOf('.'));

      if (!allowedTypes.includes(fileExtension)) {
        setErrors(prev => ({ ...prev, file: 'Please upload a CSV or Excel file' }));
        return;
      }

      setFormData(prev => ({ ...prev, file }));
      setErrors(prev => ({ ...prev, file: '' }));
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileUpload(e.dataTransfer.files[0]);
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.jobName.trim()) {
      newErrors.jobName = 'Job name is required';
    }

    if (jobType === 'search') {
      if (!formData.searchQuery.trim()) {
        newErrors.searchQuery = 'Search query is required';
      }
    } else {
      if (!formData.file) {
        newErrors.file = 'File upload is required';
      }
    }

    if (!formData.proxyUrl.trim()) {
      newErrors.proxyUrl = 'Proxy URL is required';
    } else {
      const proxyRegex = /^(https?|socks5):\/\/.+/;
      if (!proxyRegex.test(formData.proxyUrl)) {
        newErrors.proxyUrl = 'Please enter a valid proxy URL (http, https, or socks5)';
      }
    }

    // Account selection validation
    if (formData.accountSelectionMode === 'manual' && formData.selectedAccountIds.length === 0) {
      newErrors.accounts = 'Please select at least one LinkedIn account';
    }

    if (availableAccounts.length === 0) {
      newErrors.accounts = 'No LinkedIn accounts available. Please add accounts first.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      onSubmit(formData);
      handleClose();
    }
  };

  const selectedJobType = jobTypes.find(type => type.id === jobType);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <h2 className="text-xl font-semibold text-gray-900">
            {step === 1 ? 'Create New Job' : `Configure ${selectedJobType?.name}`}
          </h2>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        {/* Step 1: Job Type Selection */}
        {step === 1 && (
          <div className="p-6">
            <p className="text-gray-600 mb-6">
              Choose the type of LinkedIn scraping job you want to create:
            </p>

            <div className="space-y-4">
              {jobTypes.map((type) => {
                const Icon = type.icon;
                return (
                  <button
                    key={type.id}
                    onClick={() => handleJobTypeSelect(type.id)}
                    className="w-full p-4 border-2 border-gray-200 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-all duration-200 text-left group"
                  >
                    <div className="flex items-start space-x-4">
                      <div className={`${type.color} p-3 rounded-lg text-white group-hover:scale-110 transition-transform`}>
                        <Icon className="h-6 w-6" />
                      </div>
                      <div className="flex-1">
                        <h3 className="font-semibold text-gray-900 group-hover:text-blue-600">
                          {type.name}
                        </h3>
                        <p className="text-gray-600 text-sm mt-1">
                          {type.description}
                        </p>
                        <p className="text-blue-600 text-xs mt-2 font-medium">
                          Input: {type.fileTypes}
                        </p>
                      </div>
                    </div>
                  </button>
                );
              })}
            </div>
          </div>
        )}

        {/* Step 2: Job Configuration */}
        {step === 2 && (
          <form onSubmit={handleSubmit} className="p-6">
            {/* Job Name */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Job Name *
              </label>
              <input
                type="text"
                value={formData.jobName}
                onChange={(e) => setFormData(prev => ({ ...prev, jobName: e.target.value }))}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.jobName ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Enter a name for this job"
              />
              {errors.jobName && (
                <p className="text-red-500 text-sm mt-1">{errors.jobName}</p>
              )}
            </div>

            {/* File Upload or Search Query */}
            {jobType === 'search' ? (
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Search Query *
                </label>
                <input
                  type="text"
                  value={formData.searchQuery}
                  onChange={(e) => setFormData(prev => ({ ...prev, searchQuery: e.target.value }))}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.searchQuery ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Enter LinkedIn search keywords (e.g., 'Software Engineer San Francisco')"
                />
                {errors.searchQuery && (
                  <p className="text-red-500 text-sm mt-1">{errors.searchQuery}</p>
                )}
              </div>
            ) : (
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Upload File *
                </label>
                <div
                  className={`border-2 border-dashed rounded-lg p-6 text-center transition-colors ${
                    dragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300'
                  } ${errors.file ? 'border-red-500' : ''}`}
                  onDragEnter={handleDrag}
                  onDragLeave={handleDrag}
                  onDragOver={handleDrag}
                  onDrop={handleDrop}
                >
                  <DocumentArrowUpIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  {formData.file ? (
                    <div>
                      <p className="text-green-600 font-medium">{formData.file.name}</p>
                      <p className="text-gray-500 text-sm">File uploaded successfully</p>
                    </div>
                  ) : (
                    <div>
                      <p className="text-gray-600 mb-2">
                        Drag and drop your CSV/Excel file here, or
                      </p>
                      <input
                        type="file"
                        accept=".csv,.xlsx,.xls"
                        onChange={(e) => handleFileUpload(e.target.files[0])}
                        className="hidden"
                        id="file-upload"
                      />
                      <label
                        htmlFor="file-upload"
                        className="inline-block bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 cursor-pointer transition-colors"
                      >
                        Choose File
                      </label>
                    </div>
                  )}
                </div>
                {errors.file && (
                  <p className="text-red-500 text-sm mt-1">{errors.file}</p>
                )}
                <p className="text-gray-500 text-xs mt-2">
                  Supported formats: CSV, Excel (.xlsx, .xls)
                </p>
              </div>
            )}

            {/* Account Selection */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                LinkedIn Accounts *
              </label>

              {availableAccounts.length === 0 ? (
                <div className="p-4 bg-yellow-50 border border-yellow-200 rounded-md">
                  <p className="text-yellow-800 text-sm">
                    No LinkedIn accounts available. Please add accounts first in the LinkedIn Accounts section.
                  </p>
                </div>
              ) : (
                <div className="space-y-4">
                  {/* Account Selection Mode */}
                  <div className="flex space-x-4">
                    <label className="flex items-center">
                      <input
                        type="radio"
                        name="accountMode"
                        value="manual"
                        checked={formData.accountSelectionMode === 'manual'}
                        onChange={(e) => setFormData(prev => ({ ...prev, accountSelectionMode: e.target.value }))}
                        className="mr-2"
                      />
                      <span className="text-sm">Manual Selection</span>
                    </label>
                    <label className="flex items-center">
                      <input
                        type="radio"
                        name="accountMode"
                        value="auto_rotate"
                        checked={formData.accountSelectionMode === 'auto_rotate'}
                        onChange={(e) => setFormData(prev => ({ ...prev, accountSelectionMode: e.target.value }))}
                        className="mr-2"
                      />
                      <span className="text-sm">Auto-Rotate All</span>
                    </label>
                    <label className="flex items-center">
                      <input
                        type="radio"
                        name="accountMode"
                        value="load_balance"
                        checked={formData.accountSelectionMode === 'load_balance'}
                        onChange={(e) => setFormData(prev => ({ ...prev, accountSelectionMode: e.target.value }))}
                        className="mr-2"
                      />
                      <span className="text-sm">Load Balance</span>
                    </label>
                  </div>

                  {/* Manual Account Selection */}
                  {formData.accountSelectionMode === 'manual' && (
                    <div className="max-h-40 overflow-y-auto border border-gray-300 rounded-md p-3">
                      {availableAccounts.map((account) => (
                        <label key={account.id} className="flex items-center mb-2 last:mb-0">
                          <input
                            type="checkbox"
                            checked={formData.selectedAccountIds.includes(account.id)}
                            onChange={(e) => {
                              if (e.target.checked) {
                                setFormData(prev => ({
                                  ...prev,
                                  selectedAccountIds: [...prev.selectedAccountIds, account.id]
                                }));
                              } else {
                                setFormData(prev => ({
                                  ...prev,
                                  selectedAccountIds: prev.selectedAccountIds.filter(id => id !== account.id)
                                }));
                              }
                            }}
                            className="mr-3"
                          />
                          <div className="flex-1">
                            <div className="text-sm font-medium text-gray-900">
                              {account.account_name}
                            </div>
                            <div className="text-xs text-gray-500">
                              {account.email} • {account.requests_today || 0}/{account.daily_request_limit} requests today
                            </div>
                          </div>
                          <div className="text-xs">
                            <span className={`px-2 py-1 rounded-full ${
                              account.validation_status === 'valid' ? 'bg-green-100 text-green-800' :
                              account.validation_status === 'invalid' ? 'bg-red-100 text-red-800' :
                              'bg-gray-100 text-gray-800'
                            }`}>
                              {account.validation_status || 'Unknown'}
                            </span>
                          </div>
                        </label>
                      ))}
                    </div>
                  )}

                  {/* Auto-rotate and Load Balance Info */}
                  {formData.accountSelectionMode === 'auto_rotate' && (
                    <div className="p-3 bg-blue-50 border border-blue-200 rounded-md">
                      <p className="text-blue-800 text-sm">
                        <UsersIcon className="h-4 w-4 inline mr-1" />
                        Job will automatically rotate across all {availableAccounts.length} available accounts
                      </p>
                    </div>
                  )}

                  {formData.accountSelectionMode === 'load_balance' && (
                    <div className="p-3 bg-purple-50 border border-purple-200 rounded-md">
                      <p className="text-purple-800 text-sm">
                        <UsersIcon className="h-4 w-4 inline mr-1" />
                        Job will intelligently distribute workload across all {availableAccounts.length} accounts based on usage and cooldowns
                      </p>
                    </div>
                  )}
                </div>
              )}

              {errors.accounts && (
                <p className="text-red-500 text-sm mt-1">{errors.accounts}</p>
              )}
            </div>

            {/* Action Buttons */}
            <div className="flex justify-between pt-4 border-t">
              <button
                type="button"
                onClick={() => setStep(1)}
                className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors"
              >
                ← Back
              </button>
              <div className="space-x-3">
                <button
                  type="button"
                  onClick={handleClose}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-6 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors"
                >
                  Create Job
                </button>
              </div>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default NewJobModal;
