import axios from 'axios';

// ---- Lightweight log-level gating to reduce noisy logs ----
const LOG_LEVEL = (process.env.REACT_APP_LOG_LEVEL || (typeof window !== 'undefined' && localStorage.getItem('LOG_LEVEL')) || 'info').toLowerCase();
const LEVELS = { error: 0, warn: 1, info: 2, debug: 3 };
function shouldLog(level) {
  const current = LEVELS[LOG_LEVEL] ?? 2;
  const target = LEVELS[level] ?? 2;
  return target <= current;
}
function log(level, ...args) {
  if (!shouldLog(level)) return;
  const fn = level === 'debug' ? 'log' : level;
  try { console[fn](...args); } catch (_) { /* no-op */ }
}

// Normalize login responses from different backend implementations
// Returns a consistent shape: { success, authToken, user, refreshToken, message }
function normalizeLoginResponse(respData) {
  try {
    const data = respData && respData.data ? respData.data : respData;

    // Capture token across multiple possible field names
    let authToken = (
      data?.accessToken ||
      data?.data?.accessToken ||
      data?.token ||
      data?.data?.token ||
      data?.authToken ||
      data?.data?.authToken ||
      data?.access_token ||
      data?.data?.access_token ||
      data?.jwt ||
      data?.data?.jwt ||
      data?.jwtToken ||
      data?.data?.jwtToken ||
      data?.auth_token ||
      data?.data?.auth_token ||
      null
    );

    // Fallback: if token not present in body, try cookie `token`
    if (!authToken && typeof document !== 'undefined') {
      try {
        const m = document.cookie && document.cookie.match(/(?:^|;\s*)token=([^;]+)/);
        if (m && m[1]) {
          authToken = decodeURIComponent(m[1]);
        }
      } catch (_) {}
    }

    const user = (
      data?.user ||
      data?.data?.user ||
      data?.profile ||
      null
    );

    const refreshToken = (
      data?.refreshToken ||
      data?.data?.refreshToken ||
      null
    );

    // Consider login successful ONLY when we have a token
    const success = Boolean(authToken);

    if (!success) {
      const message = (
        data?.message ||
        data?.error ||
        (typeof data === 'string' ? data : 'Login failed')
      );
      return { success: false, message };
    }

    return {
      success: true,
      authToken,
      user,
      refreshToken,
      message: data?.message || 'Login successful',
    };
  } catch (err) {
    return { success: false, message: 'Login response parsing error' };
  }
}

// ---- Backend base URL resolution and fallback helpers ----
function isValidHttpUrl(str) {
  try {
    if (!str || typeof str !== 'string') return false;
    const u = new URL(str);
    return u.protocol === 'http:' || u.protocol === 'https:';
  } catch {
    return false;
  }
}

function deriveCandidateBases() {
  // Read env and normalize for trailing slashes to avoid double-slash URLs
  let envBase = process.env.REACT_APP_API_URL;
  if (typeof envBase === 'string') {
    envBase = envBase.trim();
    // Strip trailing slash to ensure endpoint concatenation is consistent
    if (envBase.endsWith('/')) envBase = envBase.slice(0, -1);
  }
  const storedBase = typeof window !== 'undefined' ? localStorage.getItem('API_BASE_URL') : null;
  const fromOrigin = typeof window !== 'undefined' && window.location?.origin
    ? window.location.origin
    : null;
  const rawCandidates = [envBase, storedBase, fromOrigin, 'http://localhost:5001', 'https://scralytics.com'];
  const unique = Array.from(new Set(rawCandidates.filter(Boolean)));
  return unique.filter(isValidHttpUrl);
}

function getInitialBaseURL() {
  const list = deriveCandidateBases();
  return list[0] || 'http://localhost:5001';
}

function persistBaseURL(base) {
  try {
    if (typeof window !== 'undefined' && base && isValidHttpUrl(base)) {
      localStorage.setItem('API_BASE_URL', base);
      window.__API_BASE_URL__ = base;
    }
  } catch (_) {}
}

// Ensure axios has a valid baseURL; if not, set a sane fallback
function ensureValidBaseURL(current) {
  if (isValidHttpUrl(current)) return current;
  const fallback = getInitialBaseURL();
  return isValidHttpUrl(fallback) ? fallback : 'http://localhost:5001';
}

// Create axios instance with resilient base URL
const api = axios.create({
  baseURL: getInitialBaseURL(),
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
  params: {
    _t: Date.now() // Cache busting parameter
  }
});

// ---- Token refresh coordination (avoid parallel refresh storms) ----
let isRefreshingToken = false;
let refreshWaiters = [];
const subscribeTokenRefresh = (cb) => refreshWaiters.push(cb);
const notifyTokenRefreshed = (token) => {
  try {
    refreshWaiters.forEach((cb) => cb(token));
  } finally {
    refreshWaiters = [];
  }
};

// Request interceptor with logging and auth
api.interceptors.request.use(
  (config) => {
    // Guard against invalid baseURL before axios constructs the request
    const fixedBase = ensureValidBaseURL(config.baseURL || api.defaults.baseURL);
    if (fixedBase !== config.baseURL) {
      config.baseURL = fixedBase;
      api.defaults.baseURL = fixedBase;
      log('warn', '🔧 Fixed invalid baseURL; using:', fixedBase);
    }

  // Add logging for debugging
  log('info', '🌐 API Request:', {
    method: config.method?.toUpperCase(),
    url: config.url || '(no-url)',
    baseURL: config.baseURL || '(no-baseURL)',
    fullURL: (() => {
      const base = config.baseURL || api.defaults.baseURL;
      const path = config.url || '';
      if (!base) return path || '(no-url)';
      // Avoid double slashes when concatenating
      const normalizedBase = base.endsWith('/') ? base.slice(0, -1) : base;
      const normalizedPath = path.startsWith('/') ? path : `/${path}`;
      return `${normalizedBase}${normalizedPath}`;
    })()
  });
    
    // Token is set via authAPI.setAuthToken() - no need to set here
    // Debug token presence
    let token = (
      localStorage.getItem('authToken') ||
      localStorage.getItem('token') ||
      localStorage.getItem('accessToken') ||
      localStorage.getItem('access_token') ||
      localStorage.getItem('jwt') ||
      localStorage.getItem('jwtToken') ||
      localStorage.getItem('auth_token')
    );
    if (token && token.includes('{')) {
      try {
        const parsedToken = JSON.parse(token);
        token = parsedToken.token || token;
      } catch (e) {
        log('error', 'Failed to parse token from localStorage', e);
      }
    }
    const authHeader = config.headers.Authorization || config.headers.common?.Authorization;
    log('debug', '🔐 Frontend Token Debug:', { 
      tokenInStorage: token ? token.substring(0, 20) + '...' : 'null',
      authHeaderSet: authHeader ? authHeader.substring(0, 30) + '...' : 'null'
    });
    
    if (!authHeader && token) {
      log('warn', '⚠️ Token exists in storage but not in headers - setting manually');
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    log('error', '❌ Request Error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor with enhanced logging
api.interceptors.response.use(
  (response) => {
    // Log successful responses
    log('info', '✅ API Response:', {
      status: response.status,
      url: response.config?.url,
      data: response.data
    });
    
    // Check for new token in response headers
    const newToken = response.headers['x-new-token'];
    if (newToken) {
      // Persist and apply refreshed token
      try {
        localStorage.setItem('token', newToken);
        localStorage.setItem('authToken', newToken);
      } catch (_) {}
      api.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
      log('info', '🔄 Applied refreshed token from X-New-Token header');
    }

    return response.data;
  },
  async (error) => {
    // Enhanced error logging
    log('error', '❌ API Response Error:', {
      message: error.message,
      status: error.response?.status,
      statusText: error.response?.statusText,
      url: error.config?.url,
      data: error.response?.data,
      headers: error.response?.headers,
      code: error.code,
      fullError: error
    });
    
    // Add user-friendly error message
    if (error.response?.status === 500) {
      log('error', '🔥 Server Error: The server encountered an internal error. Please try again later.');
    } else if (error.response?.status === 404) {
      log('warn', '🔍 Not Found: The requested resource was not found.');
    } else if (error.response?.status === 403) {
      log('warn', '🚫 Forbidden: You do not have permission to access this resource.');
    }
    
    const status = error.response?.status;
    const originalRequest = error.config || {};
    const url = originalRequest?.url || '';
    const isAuthEndpoint = url.includes('/api/auth/login') || url.includes('/api/auth/register') || url.includes('/api/auth/refresh');

    // Handle 401 with automatic refresh + retry (except on auth endpoints)
    if (status === 401 && !isAuthEndpoint) {
      const refreshToken = (
        localStorage.getItem('refreshToken') ||
        localStorage.getItem('refresh_token') ||
        ''
      );
      if (!refreshToken) {
        log('warn', '🔐 401 received and no refreshToken available');
        const errorMessage = error.response?.data?.message || error.message || 'Unauthorized';
        return Promise.reject({
          message: errorMessage,
          status,
          response: error.response,
          originalError: error
        });
      }

      // If a refresh is already in progress, queue this request
      if (isRefreshingToken) {
        log('info', '⏳ Waiting for ongoing token refresh to complete');
        return new Promise((resolve) => {
          subscribeTokenRefresh((newToken) => {
            // Apply new token and retry original request
            originalRequest.headers = originalRequest.headers || {};
            originalRequest.headers['Authorization'] = `Bearer ${newToken}`;
            resolve(api.request(originalRequest));
          });
        });
      }

      // Start refresh
      isRefreshingToken = true;
      try {
        log('info', '🔄 Attempting token refresh...');
        const refreshResp = await api.post('/api/auth/refresh', { refreshToken });
        const root = refreshResp && refreshResp.data ? refreshResp.data : refreshResp;
        const payload = root?.data || root;
        const newToken = (
          payload?.accessToken || payload?.token || payload?.authToken || null
        );
        const newRefresh = (
          payload?.refreshToken || null
        );

        if (!newToken) {
          throw new Error('Failed to refresh access token');
        }

        // Persist and apply new tokens
        try {
          localStorage.setItem('token', newToken);
          localStorage.setItem('authToken', newToken);
          if (newRefresh) {
            localStorage.setItem('refreshToken', newRefresh);
            localStorage.setItem('refresh_token', newRefresh);
          }
        } catch (_) {}
        api.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
        log('info', '✅ Token refresh succeeded; retrying original request');

        // Notify queued requests
        notifyTokenRefreshed(newToken);

        // Best-effort: notify extension/content script of new token
        try {
          const userStr = localStorage.getItem('user');
          const user = userStr ? JSON.parse(userStr) : null;
          const apiBase = (typeof window !== 'undefined' ? window.location.origin : '');
          if (typeof window !== 'undefined') {
            window.dispatchEvent(new CustomEvent('scralytics:loginSuccess', {
              detail: { authToken: newToken, refreshToken: newRefresh, user, apiBaseUrl: apiBase }
            }));
          }
        } catch (_) {}

        // Retry original request with the new token
        originalRequest.headers = originalRequest.headers || {};
        originalRequest.headers['Authorization'] = `Bearer ${newToken}`;
        return api.request(originalRequest);
      } catch (refreshErr) {
        log('error', '❌ Token refresh failed:', refreshErr);
        // Do not auto-clear app state, but remove invalid tokens
        try {
          localStorage.removeItem('authToken');
          localStorage.removeItem('token');
          // Keep refreshToken to allow manual retry if desired
        } catch (_) {}
        return Promise.reject({
          message: refreshErr?.message || 'Token refresh failed',
          status,
          response: error.response,
          originalError: error
        });
      } finally {
        isRefreshingToken = false;
      }
    }

    // Return error in consistent format
    const errorMessage = error.response?.data?.message || error.message || 'An error occurred';
    return Promise.reject({
      message: errorMessage,
      status: error.response?.status,
      response: error.response,
      originalError: error
    });
  }
);

// Rate limiting state
let loginInProgress = false;
let lastLoginAttempt = 0;
const LOGIN_COOLDOWN = 2000; // 2 seconds between login attempts

// Auth API
export const authAPI = {
  // Set auth token
  setAuthToken: (token) => {
    if (token) {
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      delete api.defaults.headers.common['Authorization'];
    }
  },
  
  // Clear auth token
  clearAuthToken: () => {
    delete api.defaults.headers.common['Authorization'];
  },
  
  // Login with rate limiting
  login: async (email, password) => {
    log('info', '🔐 Login attempt triggered for:', email);
    
    // Prevent multiple parallel logins
    if (loginInProgress) {
      log('warn', '⚠️ Login already in progress, ignoring duplicate request');
      return Promise.reject({ message: 'Login already in progress' });
    }
    
    // Check cooldown period
    const now = Date.now();
    const timeSinceLastAttempt = now - lastLoginAttempt;
    if (timeSinceLastAttempt < LOGIN_COOLDOWN) {
      const remainingCooldown = LOGIN_COOLDOWN - timeSinceLastAttempt;
      log('warn', `⏰ Login cooldown active, wait ${remainingCooldown}ms`);
      return Promise.reject({ 
        message: `Please wait ${Math.ceil(remainingCooldown / 1000)} seconds before trying again` 
      });
    }
    
    loginInProgress = true;
    lastLoginAttempt = now;
    
    try {
      // Prefer canonical endpoint first
      const respData = await api.post('/api/auth/login', { email, password });
      log('info', '✅ Login successful via /api/auth/login');
      // Normalize response shape
      const normalized = normalizeLoginResponse(respData);
      // Persist tokens, set auth header, and notify extension via event
      if (normalized && normalized.success && normalized.authToken) {
        try {
          // Store access token under common keys for the content script to discover
          localStorage.setItem('authToken', normalized.authToken);
          localStorage.setItem('token', normalized.authToken);
          localStorage.setItem('accessToken', normalized.authToken);
          localStorage.setItem('access_token', normalized.authToken);
          // Store refresh token under both variants
          if (normalized.refreshToken) {
            localStorage.setItem('refreshToken', normalized.refreshToken);
            localStorage.setItem('refresh_token', normalized.refreshToken);
          }
          // Persist user info if available
          if (normalized.user) {
            try { localStorage.setItem('user', JSON.stringify(normalized.user)); } catch (_) {}
          }
        } catch (_) {}

        // Apply token to axios instance
        try { authAPI.setAuthToken(normalized.authToken); } catch (_) {}

        // Derive and persist API base for the extension
        const apiBase = (typeof window !== 'undefined' && window.location && window.location.origin) ? window.location.origin : (api.defaults?.baseURL || 'http://localhost:5001');
        try { localStorage.setItem('apiBaseUrl', apiBase); } catch (_) {}

        // Explicitly notify the content script of login success to avoid UI-based guesswork
        try {
          if (typeof window !== 'undefined') {
            window.dispatchEvent(new CustomEvent('scralytics:loginSuccess', {
              detail: {
                authToken: normalized.authToken,
                refreshToken: normalized.refreshToken || null,
                user: normalized.user || null,
                apiBaseUrl: apiBase
              }
            }));
          }
        } catch (_) {}
      }
      return normalized;
    } catch (error) {
      log('error', '❌ Login failed:', error.message);
      
      // Handle specific error cases
      if (error.response?.status === 429) {
        log('warn', '🚫 Too many login attempts detected');
        const e = new Error('Too many login attempts. Please wait before trying again.');
        e.status = 429;
        e.response = error.response;
        throw e;
      }
      
      if (error.response?.status === 431) {
        log('warn', '📦 Request headers too large - clearing cookies');
        // Clear potentially oversized cookies
        document.cookie.split(";").forEach(function(c) { 
          document.cookie = c.replace(/^ +/, "").replace(/=.*/, "=;expires=" + new Date().toUTCString() + ";path=/"); 
        });
        const e = new Error('Request headers too large. Cookies cleared, please try again.');
        e.status = 431;
        e.response = error.response;
        throw e;
      }
      
      // If we hit a network error (no response), automatically try alternative bases
      const isNetworkError = !error.response && (/Network Error/i.test(error.message) || error.code === 'ERR_NETWORK');
      if (isNetworkError) {
        log('warn', '🌐 Network error detected. Trying alternative API bases...');
        const candidates = deriveCandidateBases().filter(b => b && b !== api.defaults.baseURL);
        for (const base of candidates) {
          try {
            log('info', '🔁 Attempting login against base:', base);
            api.defaults.baseURL = base;
            const altResp = await api.post('/api/auth/login', { email, password });
            const normalized = normalizeLoginResponse(altResp);
            if (normalized.success) {
              log('info', '✅ Login successful after base switch:', base);
              // Persist tokens and notify extension
              try {
                localStorage.setItem('authToken', normalized.authToken);
                localStorage.setItem('token', normalized.authToken);
                localStorage.setItem('accessToken', normalized.authToken);
                localStorage.setItem('access_token', normalized.authToken);
                if (normalized.refreshToken) {
                  localStorage.setItem('refreshToken', normalized.refreshToken);
                  localStorage.setItem('refresh_token', normalized.refreshToken);
                }
                if (normalized.user) {
                  try { localStorage.setItem('user', JSON.stringify(normalized.user)); } catch (_) {}
                }
              } catch (_) {}
              try { authAPI.setAuthToken(normalized.authToken); } catch (_) {}
              const apiBase = (typeof window !== 'undefined' && window.location && window.location.origin) ? window.location.origin : (api.defaults?.baseURL || base);
              try { localStorage.setItem('apiBaseUrl', apiBase); } catch (_) {}
              try {
                if (typeof window !== 'undefined') {
                  window.dispatchEvent(new CustomEvent('scralytics:loginSuccess', {
                    detail: {
                      authToken: normalized.authToken,
                      refreshToken: normalized.refreshToken || null,
                      user: normalized.user || null,
                      apiBaseUrl: apiBase
                    }
                  }));
                }
              } catch (_) {}
              persistBaseURL(base);
              return normalized;
            }
          } catch (e) {
            const msg = e?.message || e?.response?.data?.message || 'unknown';
            log('warn', '⚠️ Base attempt failed:', base, '-', msg);
          }
          // Try legacy path on this base if /api/login not found
          try {
            const legacyResp = await api.post('/api/login', { email, password });
            const normalized = normalizeLoginResponse(legacyResp);
            if (normalized.success) {
              log('info', '✅ Legacy /api/login successful after base switch:', base);
              // Persist tokens and notify extension
              try {
                localStorage.setItem('authToken', normalized.authToken);
                localStorage.setItem('token', normalized.authToken);
                localStorage.setItem('accessToken', normalized.authToken);
                localStorage.setItem('access_token', normalized.authToken);
                if (normalized.refreshToken) {
                  localStorage.setItem('refreshToken', normalized.refreshToken);
                  localStorage.setItem('refresh_token', normalized.refreshToken);
                }
                if (normalized.user) {
                  try { localStorage.setItem('user', JSON.stringify(normalized.user)); } catch (_) {}
                }
              } catch (_) {}
              try { authAPI.setAuthToken(normalized.authToken); } catch (_) {}
              const apiBase = (typeof window !== 'undefined' && window.location && window.location.origin) ? window.location.origin : (api.defaults?.baseURL || base);
              try { localStorage.setItem('apiBaseUrl', apiBase); } catch (_) {}
              try {
                if (typeof window !== 'undefined') {
                  window.dispatchEvent(new CustomEvent('scralytics:loginSuccess', {
                    detail: {
                      authToken: normalized.authToken,
                      refreshToken: normalized.refreshToken || null,
                      user: normalized.user || null,
                      apiBaseUrl: apiBase
                    }
                  }));
                }
              } catch (_) {}
              persistBaseURL(base);
              return normalized;
            }
          } catch (_) {}
        }
        log('error', '❌ All alternative bases failed. Returning original error.');
      }

      // Fallback: if canonical not found, try alias /api/login
      const status = (error?.response?.status ?? error?.status);
      const dataText = typeof error?.response?.data === 'string' ? error.response.data : '';
      const isNotFound = status === 404 || (dataText && dataText.includes('Cannot POST /api/auth/login'));
      if (isNotFound) {
        log('warn', '🔁 /api/auth/login not available, falling back to /api/login');
        const legacyData = await api.post('/api/login', { email, password });
        log('info', '✅ Login successful via /api/login');
        const normalized = normalizeLoginResponse(legacyData);
        // Persist tokens, set auth header, and notify extension via event
        if (normalized && normalized.success && normalized.authToken) {
          try {
            localStorage.setItem('authToken', normalized.authToken);
            localStorage.setItem('token', normalized.authToken);
            localStorage.setItem('accessToken', normalized.authToken);
            localStorage.setItem('access_token', normalized.authToken);
            if (normalized.refreshToken) {
              localStorage.setItem('refreshToken', normalized.refreshToken);
              localStorage.setItem('refresh_token', normalized.refreshToken);
            }
            if (normalized.user) {
              try { localStorage.setItem('user', JSON.stringify(normalized.user)); } catch (_) {}
            }
          } catch (_) {}
          try { authAPI.setAuthToken(normalized.authToken); } catch (_) {}
          const apiBase = (typeof window !== 'undefined' && window.location && window.location.origin) ? window.location.origin : (api.defaults?.baseURL || 'http://localhost:5001');
          try { localStorage.setItem('apiBaseUrl', apiBase); } catch (_) {}
          try {
            if (typeof window !== 'undefined') {
              window.dispatchEvent(new CustomEvent('scralytics:loginSuccess', {
                detail: {
                  authToken: normalized.authToken,
                  refreshToken: normalized.refreshToken || null,
                  user: normalized.user || null,
                  apiBaseUrl: apiBase
                }
              }));
            }
          } catch (_) {}
        }
        return normalized;
      }

      throw error;
    } finally {
      loginInProgress = false;
    }
  },
  
  // Register
  register: (userData) => {
    return api.post('/api/auth/register', userData);
  },
  
  // Get profile
  getProfile: () => {
    return api.get('/api/auth/me');
  },
  
  // Update profile
  updateProfile: (profileData) => {
    return api.put('/api/auth/me', profileData);
  },
  
  // Change password
  changePassword: (currentPassword, newPassword) => {
    return api.put('/api/auth/password', {
      currentPassword,
      newPassword,
    });
  },
  
  // Forgot password
  forgotPassword: (email) => {
    return api.post('/api/auth/forgot-password', { email });
  },
  
  // Reset password
  resetPassword: (token, password) => {
    return api.post('/api/auth/reset-password', { token, password });
  },
  
  // Refresh token
  refreshToken: (refreshToken) => {
    return api.post('/api/auth/refresh', { refreshToken });
  },
  
  // Logout
  logout: () => {
    return api.post('/api/auth/logout');
  },
};

